#!/bin/zshset -eu# CONDA UNINSTALL# Uninstall Swift/T and any cached packages from the Anaconda in PATH# Useful before reinstalling in a test installation# Report information about active Python/Conda:if ! which conda >& /dev/nullthen  print "No conda!"  return 1fiprintprint "using python:" $( which python )print "using conda: " $( which conda )printCONDA_PREFIX=${CONDA_EXE:h:h}conda uninstall swift-tSWIFTS=( $CONDA_PREFIX/pkgs/swift-t-*(/) )print "Found ${#SWIFTS} Swift/T directories"for S in $SWIFTSdo  rm -rf $SdoneSWIFTS=( $CONDA_PREFIX/pkgs/swift-t-* )for S in $SWIFTSdo  rm -v $Sdone# Local Variables:# buffer-file-coding-system: utf-8-unix# End: